// –ü–æ–ª—É—á–∞–µ–º temporary_message_id –∏–∑ –û–±—ä–µ–¥–∏–Ω–µ–Ω–∏–µ
const mergeData = $('–û–±—ä–µ–¥–∏–Ω–µ–Ω–∏–µ').item.json;
const temporary_message_id = mergeData.temporary_message_id || null;

// More out-of-the-box AI automation solutions: https://t.me/PrideAIBot
return [{
  json: { 
    joinedString: joinedString,
    chat_id: chat_id,
