  }
}];

// üì® –§–û–†–ú–ê–¢–ò–†–û–í–ê–ù–ò–ï –ò –û–¢–ü–†–ê–í–ö–ê –ê–ù–ê–õ–ò–¢–ò–ß–ï–°–ö–ò–• –û–¢–í–ï–¢–û–í

// –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö –Ω–æ–¥
const processorData = $('üìä Analytics Data Processor').item.json;
const sqlResults = $('üîç Execute Analytics Query').all();
const chatId = processorData.chat_id;
const commandType = processorData.command_type;
const userName = processorData.user_name;

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –±–µ–∑–æ–ø–∞—Å–Ω–æ–≥–æ —ç–∫—Ä–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è HTML
function escapeHtml(text) {
  if (typeof text !== 'string') return text;
  return text
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;');
}

// –ï—Å–ª–∏ SQL –∑–∞–ø—Ä–æ—Å –Ω–µ –≤—ã–ø–æ–ª–Ω–∏–ª—Å—è –∏–ª–∏ –Ω–µ—Ç –¥–∞–Ω–Ω—ã—Ö
if (!sqlResults || sqlResults.length === 0 || sqlResults[0].json.error) {
  const errorText = sqlResults?.[0]?.json?.error?.message || '–û—à–∏–±–∫–∞ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è –∑–∞–ø—Ä–æ—Å–∞';
  return [{
    json: {
      response_text: `‚ùå <b>–û–®–ò–ë–ö–ê –ê–ù–ê–õ–ò–¢–ò–ö–ò</b>\n\n–ü—Ä–∏–≤–µ—Ç, ${escapeHtml(userName)}!\n\nüîß –ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–∏ –∑–∞–ø—Ä–æ—Å–∞:\n<code>${escapeHtml(errorText)}</code>\n\nüõ†Ô∏è –û–±—Ä–∞—Ç–∏—Ç–µ—Å—å –∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—É –∏–ª–∏ –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.`,
      chat_id: chatId,
      parse_mode: 'HTML'
    }
  }];
}

// –û–±—Ä–∞–±–æ—Ç–∫–∞ –Ω–µ–∏–∑–≤–µ—Å—Ç–Ω—ã—Ö –∫–æ–º–∞–Ω–¥
if (commandType === 'unknown') {
  return [{
    json: {
      response_text: processorData.response_text,
      chat_id: chatId,
      parse_mode: 'HTML'
    }
  }];
}

// –ò–∑–≤–ª–µ–∫–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ SQL —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
const data = sqlResults.map(item => item.json);

// –§—É–Ω–∫—Ü–∏–∏ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ç–∏–ø–∞ –∫–æ–º–∞–Ω–¥—ã (HTML format)
const formatters = {
  stats: (data) => {
    let response = 'üìä <b>–°–¢–ê–¢–ò–°–¢–ò–ö–ê TELEGRAM PRIDE</b>\n\n';
    response += `üëã –ü—Ä–∏–≤–µ—Ç, ${escapeHtml(userName)}!\n\n`;

    data.forEach(row => {
      const value = typeof row.value === 'number' ? row.value.toLocaleString('ru-RU') : row.value;
      response += `${row.emoji} ${escapeHtml(row.metric)}: <b>${escapeHtml(value)}</b>\n`;
    });

    response += '\nüöÄ <i>–î–æ—Å—Ç—É–ø–Ω—ã–µ –∫–æ–º–∞–Ω–¥—ã:</i>\n';
    response += '‚Ä¢ /today - –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å —Å–µ–≥–æ–¥–Ω—è\n';
    response += '‚Ä¢ /funnel - –≤–æ—Ä–æ–Ω–∫–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π\n';
    response += '‚Ä¢ /users - —Ç–æ–ø –∞–∫—Ç–∏–≤–Ω—ã—Ö\n';
    response += '‚Ä¢ /activity - –ø–∏–∫–æ–≤—ã–µ —á–∞—Å—ã\n';
    response += '‚Ä¢ /trending - —Ç—Ä–µ–Ω–¥–∏–Ω–≥ —Å–æ–æ–±—â–µ–Ω–∏–π\n';
    response += '‚Ä¢ /health - –∑–¥–æ—Ä–æ–≤—å–µ —Å–∏—Å—Ç–µ–º—ã';

    return response;
  },

  today: (data) => {
    let response = '‚ö° <b>–ê–ö–¢–ò–í–ù–û–°–¢–¨ –°–ï–ì–û–î–ù–Ø VS –í–ß–ï–†–ê</b>\n\n';
    response += `üëã ${escapeHtml(userName)}, –≤–æ—Ç —Å—Ä–∞–≤–Ω–µ–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏:\n\n`;

    data.forEach(row => {
      const changeIcon = row.change_percent > 0 ? 'üìà' : row.change_percent < 0 ? 'üìâ' : '‚û°Ô∏è';
      const changeText = row.change_percent !== null ? 
        `${changeIcon} ${row.change_percent > 0 ? '+' : ''}${row.change_percent}%` : 'üÜï –ù–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ';

      response += `<b>${escapeHtml(row.period)}:</b>\n`;
      response += `üí¨ –°–æ–æ–±—â–µ–Ω–∏–π: ${row.message_count} ${changeText}\n`;
      response += `üë• –ê–∫—Ç–∏–≤–Ω—ã—Ö: ${row.active_users}\n`;
      response += `üìù –¢–µ–∫—Å—Ç: ${row.text_msgs} | üì∏ –§–æ—Ç–æ: ${row.photo_msgs} | üé§ –ì–æ–ª–æ—Å: ${row.voice_msgs}\n\n`;
    });

    response += 'üìà <i>–î–∏–Ω–∞–º–∏–∫–∞ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ –ø–æ–º–æ–≥–∞–µ—Ç –ø–æ–Ω—è—Ç—å —Ä–æ—Å—Ç –∞—É–¥–∏—Ç–æ—Ä–∏–∏</i>';
    return response;
  },

  funnel: (data) => {
    let response = 'üî• <b>–ö–û–ù–í–ï–†–°–ò–û–ù–ù–ê–Ø –í–û–†–û–ù–ö–ê –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–ï–ô</b>\n\n';
    response += `${escapeHtml(userName)}, —Å–º–æ—Ç—Ä–∏ –∫–∞–∫ –ø—Ä–æ—Ö–æ–¥—è—Ç –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ –ø–æ —ç—Ç–∞–ø–∞–º:\n\n`;

    data.forEach((row, index) => {
      const arrow = index === 0 ? 'üîµ' : '‚Üì üî∏';
      const barLength = Math.round(row.conversion_rate / 10);
      const progressBar = '‚ñà'.repeat(barLength) + '‚ñë'.repeat(10 - barLength);

      response += `${arrow} <b>${escapeHtml(row.stage)}</b>\n`;
      response += `${progressBar} ${row.users} —á–µ–ª. (${row.conversion_rate}%)\n\n`;
    });

    response += 'üí° <i>–í–æ—Ä–æ–Ω–∫–∞ –ø–æ–∫–∞–∑—ã–≤–∞–µ—Ç –ø—É—Ç—å –æ—Ç —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏ –¥–æ –∞–∫—Ç–∏–≤–Ω–æ–≥–æ —É—á–∞—Å—Ç–∏—è</i>';
    return response;
  },

  users: (data) => {
    let response = 'üë• <b>–¢–û–ü –ê–ö–¢–ò–í–ù–´–• –ü–û–õ–¨–ó–û–í–ê–¢–ï–õ–ï–ô</b>\n\n';
    response += `${escapeHtml(userName)}, –≤–æ—Ç —Å–∞–º—ã–µ –∞–∫—Ç–∏–≤–Ω—ã–µ —É—á–∞—Å—Ç–Ω–∏–∫–∏:\n\n`;

    data.forEach((row, index) => {
      const medals = ['ü•á', 'ü•à', 'ü•â'];
      const medal = medals[index] || `${index + 1}Ô∏è‚É£`;

      response += `${medal} <b>${escapeHtml(row.full_name)}</b>\n`;
      response += `${escapeHtml(row.username)}\n`;
      response += `üí¨ ${row.message_count} —Å–æ–æ–±—â–µ–Ω–∏–π `;
      response += `(üìù${row.text_count} üì∏${row.photo_count} üé§${row.voice_count})\n`;
      response += `${escapeHtml(row.status)}\n\n`;
    });

    response += 'üåü <i>–ê–∫—Ç–∏–≤–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ - –æ—Å–Ω–æ–≤–∞ —Å–æ–æ–±—â–µ—Å—Ç–≤–∞!</i>';
    return response;
  },

  activity: (data) => {
    let response = 'üìà <b>–ü–ò–ö–û–í–´–ï –ß–ê–°–´ –ê–ö–¢–ò–í–ù–û–°–¢–ò</b>\n\n';
    response += `${escapeHtml(userName)}, –∫–æ–≥–¥–∞ –≤–∞—à–µ —Å–æ–æ–±—â–µ—Å—Ç–≤–æ –Ω–∞–∏–±–æ–ª–µ–µ –∞–∫—Ç–∏–≤–Ω–æ:\n\n`;

    data.forEach((row, index) => {
      const barLength = Math.round(row.percentage / 5);
      const bar = '‚ñà'.repeat(barLength) + '‚ñë'.repeat(20 - barLength);
      const position = index === 0 ? 'üëë' : `${index + 1}.`;

      response += `${position} ${escapeHtml(row.time_period)}\n`;
      response += `${bar}\n`;
      response += `üìä ${row.messages} —Å–æ–æ–±—â–µ–Ω–∏–π (${row.percentage}%)\n\n`;
    });

    response += '‚è∞ <i>–ü–ª–∞–Ω–∏—Ä—É–π—Ç–µ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ –≤ –ø–∏–∫–æ–≤—ã–µ —á–∞—Å—ã –¥–ª—è –º–∞–∫—Å–∏–º–∞–ª—å–Ω–æ–≥–æ –æ—Ö–≤–∞—Ç–∞!</i>';
    return response;
  },

  trending: (data) => {
    let response = 'üìä <b>–¢–†–ï–ù–î–ò–ù–ì: –¢–ò–ü–´ –°–û–û–ë–©–ï–ù–ò–ô</b>\n\n';
    response += `${escapeHtml(userName)}, –∫–∞–∫–∏–µ —Ç–∏–ø—ã —Å–æ–æ–±—â–µ–Ω–∏–π –ø–æ–ø—É–ª—è—Ä–Ω—ã:\n\n`;

    data.forEach((row, index) => {
      const position = index + 1;
      response += `${position}. ${escapeHtml(row.message_type)} ${escapeHtml(row.trend)}\n`;
      response += `   üìä –í—Å–µ–≥–æ: <b>${row.total_count}</b> (${row.percentage}%)\n`;
      response += `   üìÖ –°–µ–≥–æ–¥–Ω—è: ${row.today_count} | –ù–µ–¥–µ–ª—è: ${row.week_count}\n\n`;
    });

    response += 'üìà <i>–ê–Ω–∞–ª–∏–∑ –ø–æ–º–æ–≥–∞–µ—Ç –ø–æ–Ω—è—Ç—å –ø—Ä–µ–¥–ø–æ—á—Ç–µ–Ω–∏—è –∞—É–¥–∏—Ç–æ—Ä–∏–∏</i>';
    return response;
  },

  health: (data) => {
    let response = 'üíö <b>–ú–û–ù–ò–¢–û–†–ò–ù–ì –ó–î–û–†–û–í–¨–Ø –°–ò–°–¢–ï–ú–´</b>\n\n';
    response += `${escapeHtml(userName)}, —Å—Ç–∞—Ç—É—Å –≤—Å–µ—Ö –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤:\n\n`;

    data.forEach(row => {
      response += `${escapeHtml(row.status)} <b>${escapeHtml(row.component)}</b>\n`;

      if (row.total_events > 0) {
        response += `üìä –û–±—Ä–∞–±–æ—Ç–∞–Ω–æ: ${row.processed_events}/${row.total_events} (${escapeHtml(row.success_percentage)})\n`;
      }
      response += '\n';
    });

    response += 'üîÑ <i>–ú–æ–Ω–∏—Ç–æ—Ä–∏–Ω–≥ –∑–∞ –ø–æ—Å–ª–µ–¥–Ω–∏–µ 24 —á–∞—Å–∞</i>\n';
    response += 'üìã <i>–í—Å–µ —Å–∏—Å—Ç–µ–º—ã —Ä–∞–±–æ—Ç–∞—é—Ç –¥–ª—è –≤–∞—à–µ–≥–æ –∫–æ–º—Ñ–æ—Ä—Ç–∞!</i>';
    return response;
  }
};

// –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –æ—Ç–≤–µ—Ç
let responseText;
try {
  if (formatters[commandType]) {
    responseText = formatters[commandType](data);
  } else {
    responseText = `‚ùì –ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π —Ç–∏–ø –∫–æ–º–∞–Ω–¥—ã: ${escapeHtml(commandType)}`;
  }
} catch (error) {
  responseText = `‚ùå –û—à–∏–±–∫–∞ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è: ${escapeHtml(error.message)}`;
}

// –î–æ–±–∞–≤–ª—è–µ–º footer —Å –≤—Ä–µ–º–µ–Ω–Ω–æ–π –º–µ—Ç–∫–æ–π
const now = new Date();
const timeString = now.toLocaleString('ru-RU', {
  timeZone: 'Europe/Moscow',
  year: 'numeric',
  month: '2-digit', 
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit'
});

responseText += `\n\n‚è∞ <i>${escapeHtml(timeString)} –ú–°–ö</i>`;
responseText += '\nü§ñ <i>Powered by @PrideAIBot Analytics</i>';

// More out-of-the-box AI automation solutions: https://t.me/PrideAIBot
return [{
  json: {
    response_text: responseText,
    chat_id: chatId,
    parse_mode: 'HTML',
    disable_web_page_preview: true,
    command_executed: commandType,
    user_name: userName,
    execution_time: timeString
  }
}];

